from crewai_tools import tool
from typing import Type
from pydantic import BaseModel, Field

class QueryInput(BaseModel):
    query: str = Field(description='Database query')
    limit: int = Field(default=10, ge=1, le=100)

@tool('query_database')
def query_database(query: str, limit: int) -> str:
    '''Execute database query and return results.'''
    try:
        results = execute_query(query, limit)
        return json.dumps(results, indent=2)
    except Exception as e:
        return f'Error: {str(e)}'

agent = Agent(
    role='Data Analyst',
    goal='Analyze data',
    backstory='Expert data analyst',
    tools=[query_database]
)

basic_flow.code_example:
from crewai.flow.flow import Flow, listen, start
from pydantic import BaseModel

class ResearchState(BaseModel):
    topic: str = ""
    research_results: str = ""
    article: str = ""
    reviewed: bool = False

class ResearchFlow(Flow[ResearchState]):

    @start()
    def set_topic(self):
        self.state.topic = "AI Agents"
        return self.state.topic

    @listen(set_topic)
    def research(self, topic):
        # Research crew runs here
        self.state.research_results = research_crew.kickoff(
            inputs={"topic": topic}
        ).raw
        return self.state.research_results

    @listen(research)
    def write_article(self, research):
        self.state.article = writing_crew.kickoff(
            inputs={"research": research}
        ).raw
        return self.state.article

# Run the flow
flow = ResearchFlow()
result = flow.kickoff()

conditional_flow.code_example:
from crewai.flow.flow import Flow, listen, start, router

class ConditionalFlow(Flow[MyState]):

    @start()
    def analyze(self):
        self.state.score = analyze_data()
        return self.state.score

    @router(analyze)
    def route_by_score(self, score):
        if score > 0.8:
            return "high_quality"
        elif score > 0.5:
            return "medium_quality"
        return "low_quality"

    @listen("high_quality")
    def handle_high(self):
        return "Approved automatically"

    @listen("medium_quality")
    def handle_medium(self):
        return "Needs review"

    @listen("low_quality")
    def handle_low(self):
        return "Rejected"

parallel_flow.code_example:
from crewai.flow.flow import Flow, listen, start, or_

class ParallelFlow(Flow[MyState]):

    @start()
    def begin(self):
        return "starting"

    @listen(begin)
    def task_a(self, _):
        # Runs in parallel with task_b
        return crew_a.kickoff()

    @listen(begin)
    def task_b(self, _):
        # Runs in parallel with task_a
        return crew_b.kickoff()

    @listen(or_(task_a, task_b))
    def combine_results(self):
        # Runs when EITHER task completes
        return self.state

knowledge_sources.code_example:
from crewai import Agent, Crew, Task
from crewai.knowledge.source.string_knowledge_source import StringKnowledgeSource
from crewai.knowledge.source.text_file_knowledge_source import TextFileKnowledgeSource

# String knowledge source
company_info = StringKnowledgeSource(
    content="Our company specializes in AI solutions...",
    metadata={"type": "company_info"}
)

# File knowledge source
docs = TextFileKnowledgeSource(
    file_path="docs/product_guide.txt"
)

# Create crew with knowledge
crew = Crew(
    agents=[support_agent],
    tasks=[support_task],
    knowledge_sources=[company_info, docs],
    embedder={
        "provider": "openai",
        "config": {"model": "text-embedding-3-small"}
    }
)

