session_state_management.code_example:
# Initialize session state
if 'counter' not in st.session_state:
    st.session_state.counter = 0

if 'user_data' not in st.session_state:
    st.session_state.user_data = {}

if 'form_data' not in st.session_state:
    st.session_state.form_data = {}

# Update session state
st.session_state.counter += 1

# Use in components
st.write(f"Counter: {st.session_state.counter}")

# Form with session state
with st.form("my_form"):
    name = st.text_input("Name")
    email = st.text_input("Email")

    submitted = st.form_submit_button("Submit")

    if submitted:
        st.session_state.form_data = {
            "name": name,
            "email": email
        }
        st.success("Form submitted!")

# Display stored data
if st.session_state.form_data:
    st.json(st.session_state.form_data)

# Clear session state
if st.button("Clear All"):
    st.session_state.clear()
    st.rerun()

# Conditional rendering based on state
if 'authenticated' not in st.session_state:
    st.session_state.authenticated = False

if not st.session_state.authenticated:
    st.warning("Please log in")
    # Login form
else:
    st.success("Welcome!")
    # Main app content

caching_patterns.code_example:
import streamlit as st
import pandas as pd
import pickle
from sklearn.ensemble import RandomForestClassifier

# Cache data (DataFrames, arrays, etc.)
@st.cache_data
def load_data(file_path: str) -> pd.DataFrame:
    """Load and cache CSV data."""
    return pd.read_csv(file_path)

@st.cache_data
def fetch_api_data(url: str) -> dict:
    """Fetch and cache API data."""
    import requests
    response = requests.get(url)
    return response.json()

@st.cache_data(ttl=3600)  # Cache for 1 hour
def get_expensive_computation(input_data: str) -> dict:
    """Expensive computation with time-to-live."""
    # Simulate expensive operation
    import time
    time.sleep(2)
    return {"result": f"Processed: {input_data}"}

# Cache resources (models, connections, etc.)
@st.cache_resource
def load_model(model_path: str) -> RandomForestClassifier:
    """Load and cache ML model."""
    with open(model_path, 'rb') as f:
        return pickle.load(f)

@st.cache_resource
def get_database_connection():
    """Create and cache database connection."""
    import sqlite3
    return sqlite3.connect('database.db')

# Usage
st.title("Caching Example")

# Data caching
if st.button("Load Data"):
    data = load_data("data.csv")
    st.dataframe(data)

# Resource caching
if st.button("Load Model"):
    model = load_model("model.pkl")
    st.success("Model loaded!")

# Clear cache
if st.button("Clear Cache"):
    st.cache_data.clear()
    st.cache_resource.clear()
    st.success("Cache cleared!")
